interface FormValues {
    name: string;
    description: string;
  }

const validationSchema = Yup.object({
    name: Yup.string().required('Task name is required'),
    description: Yup.string().max(200, 'Description must be at most 200 characters long'),
  });

export const AddTask: React.FC = React.memo((): JSX.Element => {
    const dispatch=useDispatch()
    const { register, handleSubmit, reset } = useForm<ITask>();

    const [errors, setErrors] = useState<{ name?: string; description?: string }>({});

    const handleAddTask = async(data: ITask) => {
        try {
            // Validate input data
            await validationSchema.validate(data, { abortEarly: false });

            // Dispatch addTask action if validation is successful
            dispatch(addTask({ ...data, done:false, id:Date }));
            reset(); // Reset the form after submission
            setErrors({}); // Clear errors
          } catch (err) {
            if (err instanceof Yup.ValidationError) {
              // Set errors if validation fails
              const newErrors: { name?: string; description?: string } = {};
              err.inner.forEach((error) => {
                if (error.path === 'name') newErrors.name = error.message;
                if (error.path === 'description') newErrors.description = error.message;
              });
              setErrors(newErrors);
            }
          }

        // console.log(data);
        // // dispatch(addTask({...data, done:false, id:Date.now()}));

        // const validationErrors =await validationSchema.validateSync(data);
        // console.log(validationErrors);

