        //   const newErrors: { name?: string; description?: string } = {};
        // //   validationErrors.forEach((error:any) => {
        // //     if (error.includes('name')) newErrors.name = 'Task name is required';
        // //     if (error.includes('description')) newErrors.description = 'Description must be at most 200 characters long';
        // //   });
        //   setErrors(newErrors);
        // }
      };

      return (
        <form onSubmit={handleSubmit(handleAddTask)} className="add-task-container">
          <input
            type="text"
            placeholder="Task Name"
            {...register('name')}
            className="input"
          />
          {errors.name && <span className="error">{errors.name}</span>} 
          <textarea
            placeholder="Task Description"
            {...register('description')}
            className="textarea"
          />
          {errors.description && <span className="error">{errors.description}</span>}

          <button type="submit" className="add-task-btn">Add Task</button>
        </form>
      );
});
