import * as Yup from 'yup';

interface FormValues {
    name: string;
    description: string;
  }

  const validationSchema = Yup.object().shape({
    name: Yup.string().required('Task name is required'),
    description: Yup.string(),
  });

  export const AddTask: React.FC = (): JSX.Element => {
    const dispatch = useDispatch();

    const { register, handleSubmit, reset, formState: { errors } } = useForm<FormValues>({
      resolver: yupResolver(validationSchema), // Integrate Yup with react-hook-form
    });

    const handleAddTask: SubmitHandler<FormValues> = (data) => {
      if (data.name.trim()) {
        dispatch(addTask({ name: data.name, description: data.description }));
        reset();  // Reset the form after submission
      }
    };

    return (
      <form onSubmit={handleSubmit(handleAddTask)} className="add-task-container">
        <input
          type="text"
          placeholder="Task Name"
          {...register('name')}
          className="input"
        />
        {errors.name && <span className="error">{errors.name.message}</span>} {/* Display error for task name */}

        <input
          type="text"
          placeholder="Task Description"
          {...register('description')}
          className="input"
        />
        {errors.description && <span className="error">{errors.description.message}</span>} {/* Display error for task description */}

        <button type="submit" className="add-task-btn">Add Task</button>
      </form>
    );
  };
